import * as React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  KeyboardAvoidingView,
} from "react-native";
import { Image } from "react-native";
import { LinearGradient } from "expo-linear-gradient";

export default class SignUpScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      validEmail: true,
      errorMessage: "",
      password: "",
      loginMessage: "",
    };
  }
  validate(text) {
    emailRegex = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;

    if (emailRegex.test(text)) {
      this.setState({
        validEmail: true,
        errorMessage: "",
        email: text,
      });
    } else {
      this.setState({
        validEmail: false,
        errorMessage: "Invalid Email",
        email: text,
      });
    }
  }

  login = () => {
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/SocialAuth.php", {
      method: "post",
      body: JSON.stringify({
        action: "login",
        username: this.state.email,
        password: this.state.password,
      }),
    })
      .then((res) => res.json())
      .then(
        (result) => {
          if (result.user) {
            SecureStore.setItemAsync("session", result.user.session_token).then(
              () => {
                SecureStore.setItemAsync("user", result.user.user_id).then(
                  () => {
                    this.props.route.params.onLoggedIn();
                  }
                );
              }
            );
          } else {
            this.setState({
              loginMessage: result.message,
            });
          }
        },
        (error) => {
          alert("error!");
        }
      );
  };

  render() {
    return (
      <KeyboardAvoidingView style={styles.container} behavior="padding">
        <Image
          source={require("../assets/images/logo.png")}
          style={styles.logo}
        />
        <TextInput
          style={[
            styles.input,
            !this.state.validEmail ? styles.inputError : null,
          ]}
          onChangeText={(text) => this.validate(text)}
          placeholder="Email Address"
          textContentType="emailAddress"
        />
        <Text style={styles.errorMessage}>{this.state.errorMessage}</Text>
        <TextInput
          style={styles.input}
          onChangeText={(text) => this.setState({ password: text })}
          placeholder="Password"
          textContentType="password"
          secureTextEntry={true}
        />
        <Text style={styles.errorMessage}>{this.state.loginMessage}</Text>
        <KeyboardAvoidingView style={styles.buttonContainer}>
          <LinearGradient
            colors={["#eca400", "#da2c38"]}
            start={{ x: 0.0, y: 1.0 }}
            end={{ x: 1.0, y: 1.0 }}
            style={styles.loginBtn}
          >
            <TouchableOpacity
              style={styles.loginContainer}
              onPress={() => this.login()}
            >
              <Text style={styles.loginText}>LOGIN</Text>
            </TouchableOpacity>
          </LinearGradient>
        </KeyboardAvoidingView>
        <Text
          style={styles.forgot}
          onPress={() => this.props.navigation.navigate("Forgot Password")}
        >
          Forgot Password?
        </Text>
      </KeyboardAvoidingView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fafafa",
    padding: 30,
  },
  logo: {
    alignSelf: "center",
    width: 210,
    height: 130,
    resizeMode: "contain",
    marginBottom: 40,
  },
  tagline: {
    paddingBottom: 25,
    fontSize: 20,
    textAlign: "center",
  },
  input: {
    height: 50,
    borderColor: "#DBDBDB",
    borderWidth: 1,
    borderRadius: 5,
    padding: 10,
  },
  inputError: {
    borderColor: "#DA2C38",
  },
  errorMessage: {
    color: "#DA2C38",
  },
  buttonContainer: {
    flexDirection: "row",
    justifyContent: "space-between",
    paddingTop: 10,
  },
  signUpContainer: {
    width: "95%",
    height: "85%",
    borderRadius: 5,
    backgroundColor: "white",
    alignItems: "center",
  },
  loginContainer: {
    width: "95%",
    height: "85%",
    borderRadius: 5,
    backgroundColor: "white",
    alignItems: "center",
  },
  signUpText: {
    textAlign: "center",
    color: "#3399CC",
    padding: 6,
