import * as React from "react";
import {
  StyleSheet,
  Text,
  View,
  TextInput,
  TouchableOpacity,
  KeyboardAvoidingView,
} from "react-native";
import { Image } from "react-native";
import { LinearGradient } from "expo-linear-gradient";

export default class SignUpScreen extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      validEmail: true,
      errorMessage: "",
      successMessage: "",
    };
  }
  validate(text) {
    emailRegex = /^\w+@[a-zA-Z_]+?\.[a-zA-Z]{2,3}$/;

    if (emailRegex.test(text)) {
      this.setState({
        validEmail: true,
        errorMessage: "",
        email: text,
      });
    } else {
      this.setState({
        validEmail: false,
        errorMessage: "Invalid Email",
        email: text,
      });
    }
  }

  createAccount = () => {
    //make the api call to the authentication page
    fetch("http://stark.cse.buffalo.edu/cse410/atam/api/usercontroller.php", {
      method: "post",
      body: JSON.stringify({
        action: "getUsers",
        emailaddr: this.state.email,
      }),
    })
      .then((res) => res.json())
      .then((result) => {
        if (result.users) {
          this.setState({
            errorMessage: "A user with that email already exists!",
          });
        } else {
          return fetch(
            "http://stark.cse.buffalo.edu/cse410/atam/api/SocialAuth.php",
            {
              method: "post",
              body: JSON.stringify({
                action: "register",
                email_addr: this.state.email,
              }),
            }
          )
            .then((resp) => resp.json())
            .then((response) => {
              this.setState({
                successMessage: response.message,
              });
            })
            .catch((err) => {});
        }
      })
      .catch((err) => {});
  };

  render() {
    const email = this.state;

    return (
      <KeyboardAvoidingView style={styles.container} behavior="padding">
        <Image
          source={require("../assets/images/logo.png")}
          style={styles.logo}
        />
        <TextInput
          style={styles.input}
          onChangeText={(text) => this.validate(text, "email")}
          placeholder="Email Address"
          value={email}
          textContentType="emailAddress"
        />
        <TextInput
          style={styles.input}
          onChangeText={(text) => this.validate(text, "password")}
          placeholder="Password"
          value={password}
          textContentType="password"
          secureTextEntry={true}
        />
        <KeyboardAvoidingView style={styles.buttonContainer}>
          <LinearGradient
            colors={["#eca400", "#da2c38"]}
            start={{ x: 0.0, y: 1.0 }}
            end={{ x: 1.0, y: 1.0 }}
            style={styles.loginBtn}
          >
            <TouchableOpacity
              style={styles.loginContainer}
              onPress={() => this.onSubmit()}
            >
              <Text style={styles.loginText}>LOGIN</Text>
            </TouchableOpacity>
          </LinearGradient>
        </KeyboardAvoidingView>
        <Text
          style={styles.forgot}
          onPress={() => this.props.navigation.navigate("Forgot Password")}
        >
          Forgot Password?
        </Text>
      </KeyboardAvoidingView>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fafafa",
    padding: 30,
  },
  logo: {
    // flex: 1,
    alignSelf: "center",
    width: 210,
    height: 130,
    resizeMode: "contain",
    marginBottom: 40,
  },
  input: {
    height: 50,
    borderColor: "#DBDBDB",
    borderWidth: 1,
    marginBottom: 20,
    borderRadius: 5,
    padding: 10,
  },
  buttonContainer: {
    flexDirection: "row",
  },
  loginContainer: {
    width: "98%",
    height: "85%",
    borderRadius: 6,
    backgroundColor: "white",
    alignItems: "center",
  },
  loginText: {
    textAlign: "center",
    color: "#eca400",
    padding: 6,
    fontSize: 20,
  },
  loginBtn: {
    height: 50,
    width: "100%",
    alignItems: "center",
    justifyContent: "center",
    borderRadius: 10,
    alignSelf: "flex-end",
  },
  forgot: {
    padding: 25,
    alignSelf: "center",
    fontSize: 20,
    fontWeight: "bold",
  },
});
